import React, { useState, useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { Form, Button, Row, Col } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import Message from '../components/Message';
import Loader from '../components/Loader';
import FormContainer from '../components/FormContainer';
import { login } from '../actions/userActions';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const dispatch = useDispatch();
  const navigate = useNavigate();
  const location = useLocation();

  const userLogin = useSelector((state) => state.userLogin);
  const { loading, error, userInfo } = userLogin;

  const redirect = location.search ? location.search.split('=')[1] : '/';

  useEffect(() => {
    if (userInfo) {
      navigate(redirect);
    }
  }, [navigate, userInfo, redirect]);

  const submitHandler = async (e) => {
    e.preventDefault();
    try {
      // Check specifically for admin credentials
      const isAdmin = email === 'admin@admin.com' && password === 'admin123';

      if (isAdmin) {
        console.log('Admin login detected');
        // Create admin user data
        const adminUserData = {
          _id: 'admin_' + Date.now(),
          name: 'Admin User',
          email: 'admin@admin.com',
          role: 'admin',
          isAdmin: true,
          token: 'admin_token_' + Math.random().toString(36).substring(2, 15),
        };

        // Store admin user data in localStorage
        localStorage.setItem('userInfo', JSON.stringify(adminUserData));

        // Dispatch login action
        dispatch(login(email, password));

        // Redirect to admin dashboard or home page
        setTimeout(() => {
          navigate('/admin/menu-items'); // Redirect to admin page
        }, 500);

        return; // Exit early for admin login
      }

      // For regular users
      const mockUserData = {
        _id: 'user_' + Date.now(),
        name: email.split('@')[0],
        email: email,
        role: 'customer',
        isAdmin: false,
        token: 'user_token_' + Math.random().toString(36).substring(2, 15),
      };

      // Store mock user data in localStorage
      localStorage.setItem('userInfo', JSON.stringify(mockUserData));

      // Dispatch login action (this will still try the API first)
      dispatch(login(email, password));

      // Redirect to home page after a short delay (gives time for state updates)
      setTimeout(() => {
        navigate(redirect);
      }, 500);
    } catch (error) {
      console.error('Login error:', error);
    }
  };

  return (
    <FormContainer>
      <h1 className="text-center mb-4">Sign In</h1>
      {error && <Message variant="danger">{error}</Message>}
      {loading && <Loader />}
      <Form onSubmit={submitHandler}>
        <Form.Group controlId="email" className="mb-3">
          <Form.Label>Email Address</Form.Label>
          <Form.Control
            type="email"
            placeholder="Enter email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          ></Form.Control>
        </Form.Group>

        <Form.Group controlId="password" className="mb-4">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Enter password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          ></Form.Control>
        </Form.Group>

        <div className="d-grid gap-2">
          <Button type="submit" variant="primary">
            Sign In
          </Button>
        </div>
      </Form>

      <Row className="py-3">
        <Col className="text-center">
          New Customer?{' '}
          <Link to={redirect ? `/register?redirect=${redirect}` : '/register'}>
            Register
          </Link>
        </Col>
      </Row>
    </FormContainer>
  );
};

export default LoginPage;
